import React, { useState, useEffect, useCallback } from 'react';
import { 
  StyleSheet, 
  Text, 
  View, 
  Alert, 
  TouchableOpacity, 
  ScrollView,
  Linking,
  AppState,
  Platform,
  PermissionsAndroid,
  Image,
  Modal,
  FlatList,
  TextInput,
  Dimensions
} from 'react-native';
import { StatusBar } from 'expo-status-bar';
import * as ImagePicker from 'expo-image-picker';
import FaceRecognitionService from './src/services/FaceRecognitionService';

const { width } = Dimensions.get('window');

export default function App() {
  const [isInitialized, setIsInitialized] = useState(false);
  const [faceDbCount, setFaceDbCount] = useState(0);
  const [faceDbImages, setFaceDbImages] = useState([]);
  const [lastResult, setLastResult] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [showFaceDbModal, setShowFaceDbModal] = useState(false);
  const [showTestModal, setShowTestModal] = useState(false);
  const [testImage, setTestImage] = useState(null);
  const [newFaceName, setNewFaceName] = useState('');

  const loadFaceDbImages = useCallback(async () => {
    try {
      console.log('ðŸ”¥ Loading face database images...');
      const images = await FaceRecognitionService.getFaceDbImages();
      console.log('ðŸ”¥ Loaded images count:', images.length);
      console.log('ðŸ”¥ Loaded images filenames:', images.map(img => img.filename));
      setFaceDbImages(images);
    } catch (error) {
      console.error('ðŸ”¥ âŒ Failed to load face database images:', error);
    }
  }, []);

  const initializeApp = useCallback(async () => {
    try {
      console.log('ðŸ”¥ AGGRESSIVE LOG: Starting app initialization...');
      console.log('ðŸ”¥ Platform:', Platform.OS);
      console.log('ðŸ”¥ Current isInitialized state:', isInitialized);

      console.log('ðŸ”¥ Calling FaceRecognitionService.initialize()...');
      await FaceRecognitionService.initialize();
      console.log('ðŸ”¥ âœ… Face Recognition Service initialized successfully');

      console.log('ðŸ”¥ Loading face database...');
      const count = await FaceRecognitionService.loadFaceDatabase();
      console.log('ðŸ”¥ âœ… Database loaded with', count, 'faces');
      setFaceDbCount(count);

      console.log('ðŸ”¥ Loading face database images...');
      await loadFaceDbImages();
      console.log('ðŸ”¥ âœ… Face database images loaded');

      console.log('ðŸ”¥ Setting isInitialized to true...');
      setIsInitialized(true);
      console.log('ðŸ”¥ âœ… App initialization complete');
    } catch (error) {
      console.error('ðŸ”¥ âŒ Failed to initialize app:', error);
      console.error('ðŸ”¥ âŒ Error message:', error.message);
      console.error('ðŸ”¥ âŒ Error stack:', error.stack);
      setIsInitialized(false);
      Alert.alert('Initialization Error', error.message);
    }
  }, [loadFaceDbImages]);

  const handleDeepLink = useCallback(async () => {
    try {
      console.log('ðŸ”¥ Handling deep link...');
      const initialUrl = await Linking.getInitialURL();
      console.log('ðŸ”¥ Initial URL:', initialUrl);
      if (initialUrl) {
        processDeepLink(initialUrl);
      }
    } catch (error) {
      console.error('ðŸ”¥ âŒ Error handling initial URL:', error);
    }
  }, []);

  const handleDeepLinkEvent = useCallback((event) => {
    console.log('ðŸ”¥ Deep link event received:', event);
    if (event && event.url) {
      console.log('ðŸ”¥ Processing deep link URL:', event.url);
      processDeepLink(event.url);
    }
  }, []);

  const setupDeepLinkHandler = useCallback(() => {
    console.log('ðŸ”¥ Setting up deep link handler...');
    Linking.addEventListener('url', handleDeepLinkEvent);
    handleDeepLink(); // Handle initial URL if app was opened via deep link
    console.log('ðŸ”¥ âœ… Deep link handler setup complete');
  }, [handleDeepLinkEvent, handleDeepLink]);

  useEffect(() => {
    console.log('ðŸ”¥ useEffect triggered - isInitialized:', isInitialized);
    console.log('ðŸ”¥ Current Platform.OS:', Platform.OS);

    // Prevent re-initialization if already initialized
    if (isInitialized) {
      console.log('ðŸ”¥ Already initialized, skipping setup');
      return;
    }

    const setupApp = async () => {
      console.log('ðŸ”¥ Starting setupApp function...');

      if (Platform.OS === 'android' || Platform.OS === 'web') {
        console.log('ðŸ”¥ Platform supported, continuing setup...');

        if (Platform.OS === 'android') {
          console.log('ðŸ”¥ Requesting Android permissions...');
          await requestPermissions();
          console.log('ðŸ”¥ âœ… Android permissions completed');
        }

        // Add timeout to prevent infinite initialization
        console.log('ðŸ”¥ Setting up initialization timeout...');
        const initTimeout = setTimeout(() => {
          console.error('ðŸ”¥ âŒ Initialization timeout - forcing completion');
          setIsInitialized(true);
        }, 15000); // 15 second timeout

        try {
          console.log('ðŸ”¥ Calling initializeApp...');
          await initializeApp();
          console.log('ðŸ”¥ âœ… initializeApp completed successfully');
          clearTimeout(initTimeout);
        } catch (error) {
          console.error('ðŸ”¥ âŒ Setup error:', error);
          clearTimeout(initTimeout);
          setIsInitialized(true); // Force initialization even on error
        }

        console.log('ðŸ”¥ Setting up deep link handler...');
        setupDeepLinkHandler();
      } else {
        console.log('ðŸ”¥ âŒ Platform not supported:', Platform.OS);
        Alert.alert('Platform Not Supported', 'This app works on Android devices and web demo.');
      }
    };

    console.log('ðŸ”¥ Calling setupApp...');
    setupApp();

  }, [isInitialized, initializeApp, setupDeepLinkHandler]);

  // Separate useEffect for app state changes to avoid initialization loops
  useEffect(() => {
    const handleAppStateChange = (nextAppState) => {
      console.log('ðŸ”¥ App state changed to:', nextAppState);
      if (nextAppState === 'active' && isInitialized) {
        handleDeepLink();
      }
    };

    console.log('ðŸ”¥ Setting up AppState listener...');
    const subscription = AppState.addEventListener('change', handleAppStateChange);
    return () => {
      console.log('ðŸ”¥ Cleaning up AppState listener...');
      subscription?.remove();
    };
  }, [isInitialized, handleDeepLink]);

  const requestPermissions = async () => {
    try {
      const permissions = [
        PermissionsAndroid.PERMISSIONS.READ_EXTERNAL_STORAGE,
        PermissionsAndroid.PERMISSIONS.WRITE_EXTERNAL_STORAGE,
        PermissionsAndroid.PERMISSIONS.CAMERA,
      ];

      const granted = await PermissionsAndroid.requestMultiple(permissions);

      const allGranted = Object.values(granted).every(
        permission => permission === PermissionsAndroid.RESULTS.GRANTED
      );

      if (!allGranted) {
        Alert.alert(
          'Permissions Required',
          'This app needs storage and camera permissions to work properly.',
          [{ text: 'OK' }]
        );
      }

      // Request media library permissions for Expo
      if (Platform.OS !== 'web') {
        const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== 'granted') {
          Alert.alert('Media library permission is required to select images');
        }
      }
    } catch (error) {
      console.error('Permission request error:', error);
    }
  };



  const processDeepLink = async (url) => {
    try {
      console.log('Processing deep link:', url);

      if (url.startsWith('faceapp://match')) {
        const urlParams = new URL(url);
        const imagePath = urlParams.searchParams.get('path');

        if (imagePath) {
          setIsProcessing(true);
          const result = await FaceRecognitionService.matchFace(imagePath);
          setLastResult(result);
          setIsProcessing(false);

          Alert.alert(
            'Face Match Result',
            result.match === 'yes' 
              ? `Match found: ${result.filename} (${result.confidence})`
              : 'No match found',
            [{ text: 'OK' }]
          );
        }
      }
    } catch (error) {
      setIsProcessing(false);
      console.error('Deep link processing error:', error);
      Alert.alert('Error', 'Failed to process face recognition request');
    }
  };

  const reloadDatabase = async () => {
    try {
      console.log('ðŸ”¥ Reloading face database...');
      const count = await FaceRecognitionService.loadFaceDatabase();
      console.log('ðŸ”¥ Database loaded with', count, 'faces');
      setFaceDbCount(count);

      console.log('ðŸ”¥ Reloading face database images...');
      await loadFaceDbImages();
      console.log('ðŸ”¥ âœ… Database reload complete');

      Alert.alert('Database Reloaded', `Loaded ${count} face images`);
    } catch (error) {
      console.error('ðŸ”¥ âŒ Error reloading database:', error);
      Alert.alert('Error', `Failed to reload database: ${error.message}`);
    }
  };

  const pickImageFromGallery = async () => {
    try {
      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ['images'],
        allowsEditing: true,
        aspect: [1, 1],
        quality: 0.8,
        allowsMultipleSelection: false,
      });

      if (!result.canceled && result.assets && result.assets[0]) {
        console.log('Image selected:', result.assets[0].uri);
        return result.assets[0].uri;
      }
      console.log('No image selected or selection was canceled');
      return null;
    } catch (error) {
      console.error('Image picker error:', error);
      Alert.alert('Error', `Failed to pick image from gallery: ${error.message}`);
      return null;
    }
  };

  const addFaceToDatabase = async () => {
    try {
      console.log('ðŸ”¥ Starting image picker...');
      const imageUri = await pickImageFromGallery();
      if (!imageUri) {
        console.log('ðŸ”¥ No image selected');
        return;
      }

      console.log('ðŸ”¥ Selected image URI:', imageUri);

      // Use a more robust approach with default name and confirmation
      console.log('Showing name prompt for image:', imageUri);

      Alert.prompt(
        'Add Face to Database',
        'Enter a name for this face:',
        [
          { 
            text: 'Cancel', 
            style: 'cancel',
            onPress: () => {
              console.log('User cancelled adding face to database');
            }
          },
          {
            text: 'Add',
            onPress: async (name) => {
              console.log('ðŸ”¥ User entered name:', name);

              // Use default name if none provided
              const faceName = (name && name.trim()) ? name.trim() : `Face_${Date.now()}`;
              console.log('ðŸ”¥ Using face name:', faceName);

              try {
                // Always ensure service is properly initialized
                console.log('ðŸ”¥ Ensuring service is initialized...');
                await FaceRecognitionService.initialize();
                console.log('ðŸ”¥ âœ… Service initialization confirmed');

                const filename = `${faceName.replace(/[^a-zA-Z0-9]/g, '_')}.jpg`;
                console.log('ðŸ”¥ Adding face to database:', filename, 'from', imageUri);

                const actualFilename = await FaceRecognitionService.addImageToFaceDb(imageUri, filename);
                console.log('ðŸ”¥ âœ… Face added successfully with filename:', actualFilename);

                // Wait a moment for file system to sync
                console.log('ðŸ”¥ Waiting for file system sync...');
                await new Promise(resolve => setTimeout(resolve, 1500));
                console.log('ðŸ”¥ âœ… File system sync wait complete');

                // Reload the database and update state
                console.log('ðŸ”¥ Reloading database after adding face...');
                const count = await FaceRecognitionService.loadFaceDatabase();
                console.log('ðŸ”¥ âœ… Database loaded with', count, 'faces');
                setFaceDbCount(count);

                console.log('ðŸ”¥ Reloading face database images after adding...');
                await loadFaceDbImages();
                console.log('ðŸ”¥ âœ… Face database images reloaded');

                Alert.alert('Success', `Face added to database as ${actualFilename}. Database now has ${count} faces.`);
              } catch (error) {
                console.error('ðŸ”¥ âŒ Error adding face to database:', error);
                console.error('ðŸ”¥ âŒ Error details:', error.message);
                console.error('ðŸ”¥ âŒ Error stack:', error.stack);
                Alert.alert('Error', `Failed to add face to database: ${error.message}`);
              }
            }
          }
        ],
        'plain-text',
        `Face_${Date.now()}` // Default value
      );
    } catch (error) {
      console.error('Error in addFaceToDatabase:', error);
      Alert.alert('Error', `Failed to add face to database: ${error.message}`);
    }
  };

  const removeFaceFromDatabase = async (filename) => {
    Alert.alert(
      'Remove Face',
      `Are you sure you want to remove ${filename} from the database?`,
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Remove',
          style: 'destructive',
          onPress: async () => {
            try {
              await FaceRecognitionService.removeImageFromFaceDb(filename);
              Alert.alert('Success', 'Face removed from database');
              await reloadDatabase();
            } catch (error) {
              Alert.alert('Error', 'Failed to remove face from database');
            }
          }
        }
      ]
    );
  };

  const testCustomImage = async () => {
    try {
      const imageUri = await pickImageFromGallery();
      if (!imageUri) return;

      setTestImage(imageUri);
      setIsProcessing(true);

      const result = await FaceRecognitionService.matchFace(imageUri);
      setLastResult(result);
      setIsProcessing(false);
      setShowTestModal(true);
    } catch (error) {
      setIsProcessing(false);
      Alert.alert('Test Error', error.message);
    }
  };

  const renderFaceDbItem = ({ item }) => (
    <View style={styles.faceItem}>
      {item.uri ? (
        <Image 
          source={{ uri: item.uri }} 
          style={styles.faceImage}
          onError={(error) => console.log('Image load error for', item.filename, ':', error)}
          onLoad={() => console.log('Successfully loaded image:', item.filename)}
        />
      ) : (
        <View style={[styles.faceImage, styles.placeholderImage]}>
          <Text style={styles.placeholderText}>ðŸ“·</Text>
          <Text style={styles.placeholderText}>No Preview</Text>
        </View>
      )}
      <Text style={styles.faceFilename} numberOfLines={2}>{item.filename}</Text>
      <TouchableOpacity 
        style={styles.removeButton}
        onPress={() => removeFaceFromDatabase(item.filename)}
      >
        <Text style={styles.removeButtonText}>Ã—</Text>
      </TouchableOpacity>
    </View>
  );

  if (Platform.OS !== 'android' && Platform.OS !== 'web') {
    return (
      <View style={[styles.container, styles.centered]}>
        <Text style={styles.errorText}>Android Only</Text>
        <Text style={styles.info}>This app works on Android devices and web demo.</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <StatusBar style="auto" />

      <View style={styles.header}>
        <Text style={styles.title}>Face Recognition Service</Text>
        <Text style={styles.subtitle}>
          {isInitialized ? `âœ… Ready for ${Platform.OS}` : 'â³ Initializing...'}
        </Text>
      </View>

      <ScrollView style={styles.content}>
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Database Status</Text>
          <Text style={styles.info}>
            Face Images Loaded: {faceDbCount}
          </Text>
          <View style={styles.buttonRow}>
            <TouchableOpacity style={styles.button} onPress={reloadDatabase}>
              <Text style={styles.buttonText}>Reload Database</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.button} onPress={() => setShowFaceDbModal(true)}>
              <Text style={styles.buttonText}>View Database</Text>
            </TouchableOpacity>
          </View>
          <TouchableOpacity style={[styles.button, styles.addButton]} onPress={addFaceToDatabase}>
            <Text style={styles.buttonText}>+ Add Face from Gallery</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Face Testing</Text>
          <Text style={styles.info}>
            Test face recognition with custom images
          </Text>
          <TouchableOpacity style={[styles.button, styles.testButton]} onPress={testCustomImage}>
            <Text style={styles.buttonText}>ðŸ” Test Custom Image</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Deep Link Support</Text>
          <Text style={styles.info}>
            Listening for: faceapp://match?path=...
          </Text>
        </View>

        {isProcessing && (
          <View style={styles.section}>
            <Text style={styles.processing}>ðŸ”„ Processing face match...</Text>
          </View>
        )}

        {lastResult && (
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Last Result</Text>
            <Text style={styles.result}>
              {JSON.stringify(lastResult, null, 2)}
            </Text>
            {lastResult.confidence && (
              <View style={styles.confidenceSection}>
                <Text style={styles.confidenceLabel}>
                  Confidence Level: {(parseFloat(lastResult.confidence) * 100).toFixed(1)}%
                </Text>
                <View style={styles.confidenceBar}>
                  <View 
                    style={[
                      styles.confidenceBarFill, 
                      { 
                        width: `${parseFloat(lastResult.confidence) * 100}%`,
                        backgroundColor: parseFloat(lastResult.confidence) >= 0.90 
                          ? '#4CAF50' 
                          : parseFloat(lastResult.confidence) >= 0.75 
                            ? '#FF9800' 
                            : '#f44336'
                      }
                    ]} 
                  />
                </View>
                {parseFloat(lastResult.confidence) >= 0.90 && (
                  <Text style={styles.highConfidence}>âœ… High Confidence Match</Text>
                )}
                {parseFloat(lastResult.confidence) >= 0.75 && parseFloat(lastResult.confidence) < 0.90 && (
                  <Text style={styles.mediumConfidence}>âš ï¸ Medium Confidence - Manual Verification Recommended</Text>
                )}
              </View>
            )}
          </View>
        )}
      </ScrollView>

      {/* Face Database Modal */}
      <Modal
        visible={showFaceDbModal}
        animationType="slide"
        onRequestClose={() => setShowFaceDbModal(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <Text style={styles.modalTitle}>Face Database ({faceDbImages.length})</Text>
            <TouchableOpacity 
              style={styles.closeButton}
              onPress={() => setShowFaceDbModal(false)}
            >
              <Text style={styles.closeButtonText}>Ã—</Text>
            </TouchableOpacity>
          </View>
          <FlatList
            data={faceDbImages}
            renderItem={renderFaceDbItem}
            keyExtractor={(item) => item.filename}
            numColumns={2}
            contentContainerStyle={styles.faceGrid}
            ListEmptyComponent={
              <Text style={styles.emptyText}>No faces in database. Add some faces to get started!</Text>
            }
          />
        </View>
      </Modal>

      {/* Test Image Modal */}
      <Modal
        visible={showTestModal}
        animationType="slide"
        onRequestClose={() => setShowTestModal(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <Text style={styles.modalTitle}>Face Recognition Test</Text>
            <TouchableOpacity 
              style={styles.closeButton}
              onPress={() => setShowTestModal(false)}
            >
              <Text style={styles.closeButtonText}>Ã—</Text>
            </TouchableOpacity>
          </View>
          <ScrollView style={styles.testContent}>
            {testImage && (
              <View style={styles.testImageContainer}>
                <Text style={styles.testLabel}>Test Image:</Text>
                <Image source={{ uri: testImage }} style={styles.testImage} />
              </View>
            )}

            {lastResult && (
              <View style={styles.testResult}>
                <Text style={styles.testLabel}>Result:</Text>
